const defineStaticProp = (obj, key, value) => {
  Object.defineProperty(obj, key, {
    enumerable: false,
    configurable: false,
    writable: false,
    value,
  });

  return obj;
};

export function fetch2(url, opts) {
  const request = fetch(url, opts);
  return request
    .then(response => {
      if (!response.ok) throw response;
      return response.json();
    })
    .catch(err =>
      err.json().then(errJSON => {
        const error = new Error(err.statusText);
        defineStaticProp(error, 'response', err);
        defineStaticProp(error.response, 'data', errJSON);
        defineStaticProp(error, 'request', request);

        throw error;
      }),
    );
}
import qs from 'qs';
import { fetch2 } from './fetch';

function buildURL(url, params) {
  if (params == null) return url;

  const serializedParams = qs.stringify(params)
  if (!serializedParams) return url;

  return `${url}${url.indexOf('?') < 0 ? '?' : '&'}${serializedParams}`;
}

export { buildURL, fetch2 };
import * as React from 'react';
import { OauthReceiver } from '../OauthReceiver';
import { OauthSender } from '../OauthSender';

export function createOauthFlow(
  { authorizeUrl, tokenUrl, clientId, clientSecret, redirectUri, appName } = {},
) {
  const Sender = props => (
    <OauthSender
      authorizeUrl={authorizeUrl}
      clientId={clientId}
      redirectUri={redirectUri}
      {...props}
    />
  );

  const Receiver = props => (
    <OauthReceiver
      tokenUrl={tokenUrl}
      clientId={clientId}
      clientSecret={clientSecret}
      redirectUri={redirectUri}
      appName={appName}
      {...props}
    />
  );

  return {
    Sender,
    Receiver,
  };
}
