import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTimesCircle } from '@fortawesome/free-solid-svg-icons';

const FieldRows = (props) => {
  const { val } = props;
  return (
    <div className='form-row'>
      <div className='form-group col-md-6'>
        <label>IP {val}:</label>
        <input type='text' className='form-control' placeholder='' />
      </div>
    </div>
  );
};

function RenderTxtFields(props) {
  const { value } = props;
  const val = value && parseInt(value);
  let rows = [];
  for (let i = 1; i <= val; i++) {
    rows.push(<FieldRows key={i} val={i} />);
  }
  return (
    <div>
      {rows}
      <table className='table table-bordered bg-white mt-5'>
        <thead>
          <tr>
            <th>Name</th>
            <th>IP</th>
            <th class='text-center'>Actions</th>
          </tr>
        </thead>

        <tbody>
          <tr>
            <td>ahmed.test2.com</td>
            <td>1.1.1.2 / 3.4.5.6</td>
            <td>
              <div className='d-flex justify-content-around'>
                <span style={{ cursor: 'pointer' }}>
                  <FontAwesomeIcon
                    icon={faTimesCircle}
                    className={'text-danger'}
                  />
                </span>

                <span style={{ cursor: 'pointer' }}>
                  <FontAwesomeIcon icon={faEdit} className={'text-primary'} />
                </span>
              </div>
            </td>
          </tr>
          <tr>
            <td>ahmed.test2.com</td>
            <td>1.1.1.2 / 3.4.5.6</td>
            <td>
              <div className='d-flex justify-content-around'>
                <span style={{ cursor: 'pointer' }}>
                  <FontAwesomeIcon
                    icon={faTimesCircle}
                    className={'text-danger'}
                  />
                </span>

                <span style={{ cursor: 'pointer' }}>
                  <FontAwesomeIcon icon={faEdit} className={'text-primary'} />
                </span>
              </div>
            </td>
          </tr>
          <tr>
            <td>ahmed.test2.com</td>
            <td>1.1.1.2 / 3.4.5.6</td>
            <td>
              <div className='d-flex justify-content-around'>
                <span style={{ cursor: 'pointer' }}>
                  <FontAwesomeIcon
                    icon={faTimesCircle}
                    className={'text-danger'}
                  />
                </span>

                <span style={{ cursor: 'pointer' }}>
                  <FontAwesomeIcon icon={faEdit} className={'text-primary'} />
                </span>
              </div>
            </td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

export default RenderTxtFields;
