import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTimesCircle } from '@fortawesome/free-solid-svg-icons';
const FieldRows = (props) => {
  const { val } = props;
  return (
    <>
      <div className='row d-flex'>
        <div className=' col-md-6'>
          <div className='form-group'>
            <label className='d-block mb-2 font-weight-bold'>Host {val}:</label>
            <input type='text' className='form-control' placeholder='' />
          </div>
        </div>
        <div className=' col-md-6'>
          <div className='form-group '>
            <label className='d-block mb-2 font-weight-bold'>
              Domain {val}:
            </label>
            <input type='text' className='form-control' placeholder='' />
          </div>
        </div>
      </div>
      <table className='table table-bordered bg-white'>
        <thead>
          <thead>
            <tr>
              <th>Name</th>
              <th>IP</th>
              <th class='text-center'>Actions</th>
            </tr>
          </thead>
        </thead>
        <tbody>
          <tr>
            <td>ahmed.test2.com</td>
            <td>1.1.1.2 / 3.4.5.6</td>
            <td>
              <div className='d-flex justify-content-around'>
                <span style={{ cursor: 'pointer' }}>
                  <FontAwesomeIcon
                    icon={faTimesCircle}
                    className={'text-danger'}
                  />
                </span>

                <span style={{ cursor: 'pointer' }}>
                  <FontAwesomeIcon icon={faEdit} className={'text-primary'} />
                </span>
              </div>
            </td>
          </tr>
        </tbody>
      </table>
    </>
  );
};

function RenderMXFields(props) {
  const { value } = props;
  const val = value && parseInt(value);
  let rows = [];
  for (let i = 1; i <= val; i++) {
    rows.push(<FieldRows key={i} val={i} />);
  }
  return <div>{rows}</div>;
}

export default RenderMXFields;
